class Solution(object):
    def exist(self, board, word):
        """
        :type board: List[List[str]]
        :type word: str
        :rtype: bool
        """
        if not word:
            return True

        wd = {}

        for i in word:
            wd[i] = wd.get(i, 0) + 1

        bd = {}
        headl = []
        for i in range(len(board)):
            for j in range(len(board[0])):
                bd[board[i][j]] = bd.get(board[i][j], 0) + 1
                if board[i][j] == word[0]: headl.append((i, j))

        # Check if it is possible at all
        for i in wd:
            if bd.get(i, 0) < wd[i]: return False

        pos = 0

        def OneStepSearch(i, j, pos):
            print(i,j,pos)
            if i < 0 or i == len(board) or \
                    j < 0 or j == len(board[0]) or board[i][j] != word[pos]:
                return False

            if pos == len(word) - 1: return True

            pos += 1
            board[i][j] = ''  # mark it so you know it's visited

            for a, b in [(-1, 0), (1, 0), (0, -1), (0, 1)]:
                if OneStepSearch(i + a, j + b, pos): return True
            pos -= 1
            board[i][j] = word[pos]

            return False

        # Only start with the possible Heads
        for i, j in headl:
            if OneStepSearch(i, j, pos): return True

        return False

