# coding=utf-8
class Solution(object):
    def exist(self, board, word):
        """
        :type board: List[List[str]]
        :type word: str
        :rtype: bool
        """
        if not board or not word:
            return False

        m = len(board)
        n = len(board[0])
        len_w = len(word)

        if len_w > m * n:
            return False

        is_searched = [[0 for j in range(n)] for i in range(m)]
        found = [False]

        def DFS(i, j, index):
            #print(i, j, index)
            if index == len_w - 1:
                return True
                # 找到结果要return啊
            if i - 1 >= 0 and is_searched[i - 1][j] == 0 and board[i - 1][j] == word[index + 1]:
                is_searched[i - 1][j] = 1
                # 为什么要这么写呢
                if DFS(i - 1, j, index + 1):return True
                is_searched[i - 1][j] = 0
            if i + 1 < m and is_searched[i + 1][j] == 0 and board[i + 1][j] == word[index + 1]:
                is_searched[i + 1][j] = 1
                if DFS(i + 1, j, index + 1):return True
                is_searched[i + 1][j] = 0
            if j - 1 >= 0 and is_searched[i][j - 1] == 0 and board[i][j - 1] == word[index + 1]:
                is_searched[i][j - 1] = 1
                if DFS(i, j - 1, index + 1):return True
                is_searched[i][j - 1] = 0
            if j + 1 < n and is_searched[i][j + 1] == 0 and board[i][j + 1] == word[index + 1]:
                is_searched[i][j + 1] = 1
                if DFS(i, j + 1, index + 1):return True
                is_searched[i][j + 1] = 0
            return False


        for i in range(m):
            for j in range(n):
                if board[i][j] == word[0]:
                    #print(i,j)
                    is_searched[i][j] = 1
                    if DFS(i, j, 0):
                        return True
                    # 回溯不能忘啊
                    is_searched[i][j] = 0

        return False

